import React, { useEffect, useState } from "react";
import { fetchBookById, deleteBook, updateBook } from "../api";
import { useParams, useNavigate } from "react-router-dom";

const BookDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    loadBook();
  }, []);

  const loadBook = async () => {
    const data = await fetchBookById(id);
    if (data) {
      setBook(data);
      setQuantity(data.quantity);
    }
  };

  const handleDelete = async () => {
    await deleteBook(id);
    navigate("/");
  };

  const handleUpdate = async () => {
    await updateBook(id, { quantity });
    loadBook();
  };

  if (!book) return <div>책 정보를 불러오는 중...</div>;

  return (
    <div>
      <h1>{book.title}</h1>
      <p>저자: {book.author}</p>
      <p>설명: {book.description}</p>
      <p>수량: {quantity}</p>

      <input
        type="number"
        value={quantity}
        min="1"
        onChange={(e) => setQuantity(Number(e.target.value))}
      />
      <button onClick={handleUpdate}>수량 업데이트</button>

      <button onClick={handleDelete}>책 삭제</button>
      <button onClick={() => navigate("/")}>목록으로</button>
    </div>
  );
};

export default BookDetail;
