import { Book } from "./types";

const API_URL = "http://localhost:5000/books";

export const fetchBooks = async (query: string = "", page: number = 1): Promise<Book[]> => {
  const response = await fetch(`${API_URL}?q=${query}&_page=${page}&_limit=10`);
  return response.json();
};

export const fetchBookById = async (id: number): Promise<Book> => {
  const response = await fetch(`${API_URL}/${id}`);
  return response.json();
};

export const addBook = async (book: Omit<Book, "id">): Promise<void> => {
  await fetch(API_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(book),
  });
};

export const deleteBook = async (id: number): Promise<void> => {
  await fetch(`${API_URL}/${id}`, { method: "DELETE" });
};

export const updateBookQuantity = async (id: number, quantity: number): Promise<void> => {
  await fetch(`${API_URL}/${id}`, {
    method: "PATCH",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ quantity }),
  });
};
