import { Book } from "./types";

const API_URL = "http://localhost:5000/api/books";

// 책 목록 조회 (검색 및 페이지네이션 추가)
 export const fetchBooks = async (query: string = "", page: number = 1): Promise<{ books: Book[], totalPages: number }> => {
   const response = await fetch(`${API_URL}?q=${query}&_page=${page}&_limit=10`);
   const books = await response.json();

   // 서버에서 응답받은 총 데이터 수를 이용하여 총 페이지 수 계산
   const totalCount = response.headers.get("X-Total-Count");
   const totalPages = totalCount ? Math.ceil(Number(totalCount) / 10) : 1;
   

   return { books, totalPages };
 };


// 책 상세 정보 조회
export const fetchBookById = async (id: number): Promise<Book> => {
  const response = await fetch(`${API_URL}/${id}`);
  return response.json();
};

// 책 추가
export const addBook = async (book: Omit<Book, "id">): Promise<void> => {
  await fetch(API_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(book),
  });
};

// 책 정보 수정
export const updateBook = async (id: number, updatedData: Omit<Book, "id">): Promise<void> => {
  await fetch(`${API_URL}/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(updatedData),
  });
};

// 책 삭제
export const deleteBook = async (id: number): Promise<void> => {
  await fetch(`${API_URL}/${id}`, { method: "DELETE" });
};
