import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api/books";

export const fetchBooks = async (page, searchQuery) => {
  try {
    const response = await axios.get(API_BASE_URL, {
      params: { page, search: searchQuery },
    });
    return response.data;
  } catch (error) {
    console.error("책 목록을 가져오는 중 오류 발생:", error);
    return { books: [], totalPages: 0 };
  }
};

export const fetchBookById = async (id) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error("책 정보를 가져오는 중 오류 발생:", error);
    return null;
  }
};

export const addBook = async (bookData) => {
  try {
    await axios.post(API_BASE_URL, bookData);
  } catch (error) {
    console.error("책 추가 중 오류 발생:", error);
  }
};

export const updateBook = async (id, updatedData) => {
  try {
    await axios.put(`${API_BASE_URL}/${id}`, updatedData);
  } catch (error) {
    console.error("책 수정 중 오류 발생:", error);
  }
};

export const deleteBook = async (id) => {
  try {
    await axios.delete(`${API_BASE_URL}/${id}`);
  } catch (error) {
    console.error("책 삭제 중 오류 발생:", error);
  }
};
