import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { fetchBookById, deleteBook, updateBook } from "../api";
import { Book } from "../types";

const BookDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [book, setBook] = useState<Book | null>(null);

  useEffect(() => {
    fetchBookById(Number(id)).then(setBook);
  }, [id]);

  if (!book) return <p>로딩 중...</p>;

  const handleUpdate = (quantity: number) => {
    if (book) {
      const updatedBook = { ...book, quantity };  // book 객체에서 quantity만 변경
      updateBook(book.id, updatedBook);  // updatedBook을 넘겨줌
    }
  };

  return (
    <div>
      <h1>{book.title}</h1>
      <p>저자: {book.author}</p>
      <p>설명: {book.description}</p>
      <p>수량: {book.quantity}</p>
      <button onClick={() => handleUpdate(book.quantity + 1)}>수량 증가</button>
      <button onClick={() => handleUpdate(book.quantity - 1)}>수량 감소</button>
      <button onClick={() => { deleteBook(book.id); navigate("/"); }}>삭제</button>
    </div>
  );
};

export default BookDetail;
